import React from 'react';
import { Link } from 'react-router-dom';
import { fuHua, getMetadata, tokenURI } from '../utils/branding.js'; // 假设获取数据和图片的函数
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const EggCard = ({ tokenId }) => {
  // console.log(tokenId)
  const [url, setUrl] = useState('');
  const navigate = useNavigate();

  const [metadata, setMetadata] = useState({
    id:null,
    parentId1:null,
    parentId2:null,
    species:null,
    owner:null,
    isHatched:null,
 });
  

  const onConnectWallet=async()=>{
await fuHua(tokenId)
  }

  const goToMarket=async()=>{
navigate('/Market');
  }

  useEffect(() => {
    const getInfo = async () => {
      const _id = await getMetadata(true,tokenId,0);
      const _d1 = await getMetadata(true,tokenId,1);
      const _d2 = await getMetadata(true,tokenId,2);
      const _species=await getMetadata(true,tokenId,3);
      const _isHatched=await getMetadata(true,tokenId,5);

     setMetadata({ id:_id,
       parentId1:_d1,
       parentId2:_d2,
       species:_species,
       isHatched:_isHatched})

      const url = await tokenURI(false,tokenId);

     setUrl(url);
   }

    getInfo();
  }, []);
  return (
    <div className="nft-card" >
      <div className="nft-image">
        <img src={url} alt={"图片"} />
      </div>
      <div className="nft-info">
        <h3>{metadata}</h3>

      </div>
      <button className="connect-wallet-button" onClick={onConnectWallet}>{"孵化"||"孵化中..."}</button>
      <button className="button2" onClick={goToMarket}>go to market</button>    </div>
  );
};


export default EggCard;
