import React from 'react';
import { Link } from 'react-router-dom';
import { getMetadata, tokenURI, upload } from '../utils/branding.js'; // 假设获取数据和图片的函数
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Contract } from 'ethers';


const DradngeCard = ({ tokenId }) => {
  // console.log(tokenId)
  const [metadata, setMetadata] = useState({
     id:null,
     parentId1:null,
     parentId2:null,
     species:null,
     owner:null,
     isHatched:null,
  });

  const [url, setUrl] = useState('');
  const navigate = useNavigate();


 

      const onConnectWallet=async()=>{
await upload(true,tokenId,10000)
      }

      const goToMarket=async()=>{

        await buy(false,tokenId,10000)
    
      }
    

  useEffect(() => {
    const getInfo = async () => {
      const _id = await getMetadata(true,tokenId,0);
       const _d1 = await getMetadata(true,tokenId,1);
       const _d2 = await getMetadata(true,tokenId,2);
       const _species=await getMetadata(true,tokenId,3);
       const _isHatched=await getMetadata(true,tokenId,5);

      setMetadata({ id:_id,
        parentId1:_d1,
        parentId2:_d2,
        species:_species,
        isHatched:_isHatched})

    const url = await tokenURI(true,tokenId);
      setUrl(url);
    }
    getInfo();
  }, []);
  return (
    <div className="nft-card" >
      <div className="nft-image">
        <img src={url} alt={"图片"} />
      </div>
      <div className="nft-info">
        <h3>id:{metadata.id}</h3>
        <h3>parent1:{metadata.d1}</h3>
        <h3>parent2:{metadata.d2}</h3>
        <h3>species:{metadata.species}</h3>
        <h3>{metadata.isHatched}</h3>


      </div>
     
      <button className="connect-wallet-button" onClick={onConnectWallet}>upload</button>
      <button className="button2" onClick={goToMarket}>购买</button>    


    </div>
  );
};


export default DradngeCard;
