import React from 'react';
import { Link } from 'react-router-dom';
import { fuHua, getMetadata, tokenURI } from '../utils/branding.js'; // 假设获取数据和图片的函数
import { useState, useEffect } from 'react';
const EggCard = ({ tokenId }) => {
  // console.log(tokenId)
  const [metadata, setMetadata] = useState('');
  const [url, setUrl] = useState('');

  const onConnectWallet=async()=>{

await fuHua(tokenId)

  }

  useEffect(() => {
    const getInfo = async () => {
      const metadata = await getMetadata(false,tokenId,1);
      const url = await tokenURI(false,tokenId);

      setMetadata(metadata);
      setUrl(url);
    }
    getInfo();
  }, []);
  return (
    <div className="nft-card" >
      <div className="nft-image">
        <img src={url} alt={"图片"} />
      </div>
      <div className="nft-info">
        <h3>{metadata}</h3>

      </div>
      <button className="connect-wallet-button" onClick={onConnectWallet}>{"孵化"||"孵化中..."}</button>
      <Link to="/market">Market</Link>
    </div>
  );
};


export default EggCard;
