import React from 'react';
import { Link } from 'react-router-dom';
import { getMetadata, tokenURI } from '../utils/branding.js'; // 假设获取数据和图片的函数
import { useState, useEffect } from 'react';
const DradngeCard = ({ tokenId, onClick }) => {
  // console.log(tokenId)
  const [metadata, setMetadata] = useState('');
  const [url, setUrl] = useState('');

  useEffect(() => {
    const getInfo = async () => {
      const metadata = await getMetadata(true,tokenId,1);
      const url = await tokenURI(true,tokenId);

      setMetadata(metadata);
      setUrl(url);
    }
    getInfo();
  }, []);
  return (
    <div className="nft-card" onClick={onClick}>
      <div className="nft-image">
        <img src={"http://127.0.0.1.232:8080/ipfs/QmTu9DYJnqHxnjHhchduVuiJ8zZJtMJ4CBwJ2z6SGNsqa6"} alt={"图片"} />
      </div>
      <div className="nft-info">
        <h3>{metadata}</h3>

      </div>
      <Link to="/fuhua">Fuhua</Link>
      <Link to="/market">Market</Link>
    </div>
  );
};


export default DradngeCard;
