import React from 'react';
import { Link } from 'react-router-dom';
import { getMetadata, tokenURI } from '../utils/branding.js'; // 假设获取数据和图片的函数
import { useState, useEffect } from 'react';
const DradngeCard = ({ tokenId }) => {
  // console.log(tokenId)
  const [metadata, setMetadata] = useState('');
  const [url, setUrl] = useState('');

  const goToMarket=async()=>{
    navigate('/Market');
      }

  useEffect(() => {
    const getInfo = async () => {
      const metadata = await getMetadata(true,tokenId,1);
      const url = await tokenURI(true,tokenId);

      setMetadata(metadata);
      setUrl(url);
    }
    getInfo();
  }, []);
  return (
    <div className="nft-card" >
      <div className="nft-image">
        <img src={url} alt={"图片"} />
      </div>
      <div className="nft-info">
        <h3>{metadata}</h3>

      </div>
      <button className="button2" onClick={goToMarket}>go to market</button>  
    </div>
  );
};


export default DradngeCard;
