import { useState, useEffect } from 'react';
import DragenCard from './DragenCard.js';
import {balanceOf,getAllNFTs,getMetadata} from '../utils/branding.js';
import { useNavigate } from 'react-router-dom';


const Dragen = () => {
  //调用getallNFt方法获取所有NFT

  const [DragensData, setDragensData] = useState([]);
  const navigate = useNavigate();


  const handleCardClick = (tokenId) => {
    navigate(`/dragon-detail/${tokenId}`);
  };

  useEffect(() => {
    
    const fetchNFTs = async () => {
      const length = await balanceOf();
      console.log('length', length)
      const array = await getAllNFTs(true);
      for (let i = 0; i < length; i++) {
        const tokenId=array[i];
        console.log('i', i)
        setDragensData((prev) => [...prev, tokenId]);
        //setDragensData((prev) => [...new Set(prev)])
      }
    };
    fetchNFTs();
  }, []);

  return (
    <div>
      <h1>Dradngen Component</h1>
      <div className="dradngen-container" style={{ width: '20%' }}>
        {DragensData.map((dradngen) => (
          <DragenCard tokenId={dradngen} onClick={handleCardClick} />
        ))}
      </div>
    </div>
  );
};

export default Dragen;
