import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { balanceOf, getMetadata, tokenURI } from '../utils/branding';

const FuhuaComponent = () => {
  const [dinosaurData, setDinosaurData] = useState({});
  const [imageUrl, setImageUrl] = useState("");

  const { fuhuaId } = useParams();


  console.log(balanceOf("0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266"))
  console.log();
  useEffect(() => {
    // 通过 getDinosaurByAddress 获取数据
    const fetchData = async () => {
      try {
        const data = await getMetadata(0); // 假设这是获取数据的异步函数
        setDinosaurData(data);
      } catch (error) {
        console.error('Error fetching dinosaur data:', error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    // 通过 geturlById 获取图片url
    const fetchImageUrl = async () => {
      try {
        const url = await tokenURI(1); // 假设这是获取图片url的异步函数
        setImageUrl(url);
        console.log(url);
      } catch (error) {
        console.error('Error fetching image url:', error);
      }
    };

    fetchImageUrl();
  }, [fuhuaId]);
  console.log("1");
  console.log(getMetadata(0));
  console.log(dinosaurData);
  return (
    <div className="Fuhua">
      <h1>Fuhua Room</h1>
      <div>
        <img src={imageUrl} alt="img" />
      </div>
      <div>
        {/* 根据dinosaurData渲染其他数据 */}
        <p>data</p>
      </div>
    </div>
  );
};

export default FuhuaComponent;
